    private function createValidGroupAcl() {
        $groupAcl = new Core_Model_Acl_Group();
        $groupAcl->group = 1;
        $groupAcl->module = 'core';
        $groupAcl->controller = 'index';
        $groupAcl->action = 'new';
        $groupAcl->object = 2;
        $groupAcl->permission = 1;

        $result = $this->object->create($groupAcl);

        $this->assertInstanceOf('Core_Model_Acl_Group_Interface', $result);
        return $result;
    }

    private function createTestGroupAcls() {
        // $this->createValidGroupAcl();
        $acls = array();

        for ($i = 1; $i <= 3; $i++) {

            $base = array(
                'group' => $i,
                'module' => 'core',
                'controller' => 'test',
                'action' => 'new',
                'object' => null,
                'permission' => 1);

            $acls[] = $base;
            //core
            $acl = $base;

            $acl['action'] = 'edit';
            $acls[] = $acl;

            $acl['action'] = 'delete';
            $acls[] = $acl;

            //test
            $acl = $base;
            $acl['module'] = 'test';
            $acls[] = $acl;

            $acl['action'] = 'edit';
            $acls[] = $acl;

            $acl['action'] = 'delete';
            $acls[] = $acl;

            //specfic objects
            $acl['action'] = 'view';
            $acl['object'] = 1;
            $acls[] = $acl;

            $acl['object'] = 2;
            $acls[] = $acl;

            $acl['object'] = 3;
            $acls[] = $acl;
        }
        foreach ($acls as $acl) {
            $this->object->create($acl);
        }
    }

